    constructor(props) {
        super(props)
        this.state = {
            last_month: true,

        }
    }

    render() {
        return (
            <div>
                <div className="button-group">
                    <button className="button1">last month</button>
                    <button className="button2">last 6 months</button>
                    <button className="button3">all-time</button>
                </div>

            </div>
        )
    }
}

export default ToggleButtons




