export class ToggleButtons extends Component {
    constructor(props) {
        super(props)
        this.state = {
            button1_type: true,
            button2_type: false,
            button3_type: false,
        }
        this.handleClick1 = this.handleClick1.bind(this);
        this.handleClick2 = this.handleClick2.bind(this);
        this.handleClick3 = this.handleClick3.bind(this);
    }

    handleClick1() {
        this.setState({
            button1_type: true,
            button2_type: false,
            button3_type: false
        });
    }

    handleClick2() {
        this.setState({
            button1_type: false,
            button2_type: true,
            button3_type: false
        });
    }

    handleClick3() {
        this.setState({
            button1_type: false,
            button2_type: false,
            button3_type: true
        });
    }


    render() {
        return (
            <div>
                <div className="button-group">
                    <button onClick={this.handleClick1}>
                        last month
                    </button>
                    <button onClick={this.handleClick2}>
                        last 6 months
                        </button>
                    <button onClick={this.handleClick3}>
                        all-time
                    </button>
                </div>
                <div>
                    {this.state.button1_type && (
                        <p>
                            displaying last month
                        </p>
                    )}
                    {this.state.button2_type && (
                        <p>
                            displaying last 6 months
                        </p>
                    )}
                    {this.state.button3_type && (
                        <p>
                            displaying all-time
                        </p>
                    )}
                </div>
            </div>
        )
    }
}

export default ToggleButtons




